// src/pages/TemplateLibrary.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TemplateLibrary = () => {
  const [templates, setTemplates] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const navigate = useNavigate();

  useEffect(() => {
    fetchTemplates();
  }, []);

  const fetchTemplates = async () => {
    try {
      const res = await axios.get(`${API_URL}/templates`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTemplates(res.data);
    } catch (error) {
      console.error('Failed to fetch templates', error);
    }
  };

  const handleDownloadTemplate = (template) => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(template, null, 2));
    const anchor = document.createElement('a');
    anchor.setAttribute("href", dataStr);
    anchor.setAttribute("download", `${template.name}.json`);
    document.body.appendChild(anchor);
    anchor.click();
    anchor.remove();
  };

  const handleApplyTemplate = (template) => {
    // 儲存模板的 flowChart 到 localStorage 並導向流程圖編輯器，假設路由 /flowchart-editor/new
    localStorage.setItem('appliedTemplate', JSON.stringify(template.flowChart));
    navigate('/flowchart-editor/new');
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleImportTemplate = () => {
    if (!selectedFile) return;
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const json = JSON.parse(e.target.result);
        // 呼叫 createTemplate API
        axios.post(`${API_URL}/templates`, json, {
          headers: { Authorization: `Bearer ${token}` }
        }).then(() => {
          fetchTemplates();
          alert("Template imported successfully!");
        }).catch(err => {
          console.error(err);
          alert("Import failed");
        });
      } catch (error) {
        alert("Invalid JSON file");
      }
    };
    reader.readAsText(selectedFile);
  };

  return (
    <div>
      <h2>Template Library</h2>
      <div>
        <input type="file" accept=".json" onChange={handleFileChange} />
        <button onClick={handleImportTemplate}>Import Template</button>
      </div>
      <div>
        {templates.map(template => (
          <div key={template._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px 0" }}>
            <h3>{template.name}</h3>
            <p>{template.description}</p>
            <button onClick={() => handleDownloadTemplate(template)}>Download</button>
            <button onClick={() => handleApplyTemplate(template)} style={{ marginLeft: "10px" }}>Apply Template</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TemplateLibrary;
