// src/pages/CustomFlowChartEditor.jsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

// È†êË®≠Â∞∫ÂØ∏Ëàá Phase Â∞∫ÂØ∏
const DEFAULT_SIZE = { width: 140, height: 80 };
const PHASE_SIZE = { width: 300, height: 200 };
const RESIZE_HANDLE_SIZE = 10;
const PHASE_PADDING = 20; // Phase Ëá™ÂãïÂåÖË¶ÜÂ≠êÁØÄÈªûÊôÇÁöÑÈ°çÂ§ñÈÇäË∑ù

// Ê†πÊìöÁØÄÈªû type ËàáÊòØÂê¶ÊúâÊâãÂãïË™øÊï¥ÈÅéÂ∞∫ÂØ∏ÔºåÊ±∫ÂÆöÂØ¶ÈöõÂ∞∫ÂØ∏
const getNodeSize = (node) => {
  if (node.width && node.height) {
    return { width: node.width, height: node.height };
  }
  return node.type === 'phase' ? PHASE_SIZE : DEFAULT_SIZE;
};

// Ê†πÊìö type Áî¢Áîü‰∏çÂêåÁöÑÁØÄÈªûÂÖßÂÆπËàáÊ®£Âºè
const renderNodeContent = (node) => {
  const commonStyle = { padding: "4px", textAlign: "center", fontSize: "12px", height: "100%", boxSizing:"border-box" };
  switch(node.type) {
    case "phase":
      return (
        <div style={{ ...commonStyle, border: "2px dashed blue", backgroundColor: "#e0f7ff", overflow:"hidden" }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "task":
      return (
        <div style={{ ...commonStyle, border: "2px solid black", backgroundColor: "#fff" }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          {node.important && <div style={{ color: "red" }}>Important</div>}
          <div>{node.description}</div>
        </div>
      );
    case "subFlow":
      return (
        <div style={{ ...commonStyle, border: "2px solid gray", backgroundColor: "#f0f0f0" }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "iterative":
      return (
        <div style={{ ...commonStyle, border: "2px dotted green", backgroundColor: "#e8ffe8" }}>
          <div style={{ fontWeight: "bold" }}>{node.label} üîÑ</div>
          <div>{node.description}</div>
        </div>
      );
    case "note":
      return (
        <div style={{ ...commonStyle, border: "1px solid orange", backgroundColor: "#fff7d6" }}>
          <div style={{ fontStyle: "italic" }}>{node.text || node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "extra":
      return (
        <div style={{ ...commonStyle, fontSize:"10px" }}>
          ‚Ä¢ {node.content}
        </div>
      );
    default:
      return (
        <div style={{ ...commonStyle, border: "1px solid black" }}>
          {node.label || "No Label"}
        </div>
      );
  }
};

const CustomFlowChartEditor = () => {
  const { id: projectId } = useParams();
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const containerRef = useRef(null);

  // ÊâÄÊúâÁØÄÈªûË≥áÊñôÔºåÊ†ºÂºèÂåÖÂê´Ôºöid, type, label, description, extras, link, children, position, width, height
  const [nodes, setNodes] = useState([]);
  // ÈÅ∏ÂèñÁãÄÊÖã
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  // ÊãñÊõ≥ÁãÄÊÖã
  const [draggingNodeId, setDraggingNodeId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  // Ë™øÊï¥Â§ßÂ∞èÁãÄÊÖã
  const [resizingNodeId, setResizingNodeId] = useState(null);
  const [resizeStart, setResizeStart] = useState({ x: 0, y: 0 });
  const [initialSize, setInitialSize] = useState({ width: 0, height: 0 });

  // ËÆÄÂèñÊµÅÁ®ãÂúñË≥áÊñô
  useEffect(() => {
    const fetchFlowChart = async () => {
      try {
        const res = await axios.get(`${API_URL}/projects/${projectId}/flowchart`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNodes(res.data || []);
      } catch (error) {
        console.error("Failed to load flowchart:", error);
      }
    };
    fetchFlowChart();
  }, [projectId, API_URL, token]);

  // ‰æùÊìöÊãñÊõ≥ÁãÄÊÖãÊõ¥Êñ∞ÁØÄÈªû‰ΩçÁΩÆ
  const handleMouseMove = (e) => {
    // Ëã•Ê≠£Âú®ÊãñÊõ≥
    if (draggingNodeId && !resizingNodeId) {
      const containerRect = containerRef.current.getBoundingClientRect();
      const newX = e.clientX - containerRect.left - dragOffset.x;
      const newY = e.clientY - containerRect.top - dragOffset.y;
      setNodes(prevNodes =>
        prevNodes.map(n => {
          if (n.id === draggingNodeId) {
            const deltaX = newX - n.position.x;
            const deltaY = newY - n.position.y;
            // Â¶ÇÊûúÊòØ phaseÔºåÂ∞áÂÖ∂ÂÖßÈÉ®ÊâÄÊúâÂ≠êÁØÄÈªû‰πü‰∏ÄËµ∑ÁßªÂãï
            if (n.type === "phase" && n.children && Array.isArray(n.children)) {
              prevNodes = prevNodes.map(child => {
                if (n.children.includes(child.id)) {
                  return { ...child, position: { x: child.position.x + deltaX, y: child.position.y + deltaY } };
                }
                return child;
              });
            }
            return { ...n, position: { x: newX, y: newY } };
          }
          return n;
        })
      );
    }
    // Ëã•Ê≠£Âú®Ë™øÊï¥Â§ßÂ∞è
    if (resizingNodeId) {
      const containerRect = containerRef.current.getBoundingClientRect();
      const newWidth = Math.max(50, initialSize.width + (e.clientX - containerRect.left - resizeStart.x));
      const newHeight = Math.max(30, initialSize.height + (e.clientY - containerRect.top - resizeStart.y));
      setNodes(prevNodes =>
        prevNodes.map(n => n.id === resizingNodeId ? { ...n, width: newWidth, height: newHeight } : n)
      );
    }
  };

  const handleMouseUp = () => {
    // ÊãñÊõ≥ÁµêÊùüÂæåÔºåËã•ÊãñÊõ≥ÁöÑÊòØ phaseÔºåÈáçÊñ∞Ë®àÁÆó phase Â∞∫ÂØ∏‰ª•ÂåÖ‰ΩèÂÖ∂Â≠êÁØÄÈªû
    if (draggingNodeId) {
      const dragged = nodes.find(n => n.id === draggingNodeId);
      if (dragged && dragged.type === "phase") {
        // ÊâæÂá∫ÊâÄÊúâÂ≠êÁØÄÈªû‰ΩçÁΩÆ
        const childrenNodes = nodes.filter(n => dragged.children && dragged.children.includes(n.id));
        if (childrenNodes.length > 0) {
          const xs = childrenNodes.map(n => n.position.x);
          const ys = childrenNodes.map(n => n.position.y);
          const widths = childrenNodes.map(n => getNodeSize(n).width);
          const heights = childrenNodes.map(n => getNodeSize(n).height);
          const minX = Math.min(...xs) - PHASE_PADDING;
          const minY = Math.min(...ys) - PHASE_PADDING;
          const maxX = Math.max(...xs.map((x, i) => x + widths[i])) + PHASE_PADDING;
          const maxY = Math.max(...ys.map((y, i) => y + heights[i])) + PHASE_PADDING;
          setNodes(prevNodes =>
            prevNodes.map(n =>
              n.id === dragged.id ? { ...n, position: { x: minX, y: minY }, width: maxX - minX, height: maxY - minY } : n
            )
          );
        }
      }
    }
    setDraggingNodeId(null);
    setResizingNodeId(null);
  };

  // ÈªûÈÅ∏ÁØÄÈªûÈÄ≤Ë°åÈÅ∏Âèñ
  const handleSelectNode = (e, nodeId) => {
    e.stopPropagation();
    setSelectedNodeId(nodeId);
  };

  // Âà™Èô§ÁØÄÈªûÔºå‰∏¶ÂæûÊâÄÊúâ phase ÁöÑ children ÁßªÈô§
  const handleDeleteNode = (nodeId) => {
    setNodes(prevNodes => prevNodes
      .filter(n => n.id !== nodeId)
      .map(n => n.children && Array.isArray(n.children) ? { ...n, children: n.children.filter(cid => cid !== nodeId) } : n)
    );
    if (selectedNodeId === nodeId) setSelectedNodeId(null);
  };

  // ÈñãÂßãË™øÊï¥Â§ßÂ∞èÔºàÊãñÊõ≥Âè≥‰∏ãËßíÔºâ
  const handleResizeMouseDown = (e, nodeId) => {
    e.stopPropagation();
    setResizingNodeId(nodeId);
    const containerRect = containerRef.current.getBoundingClientRect();
    setResizeStart({ x: e.clientX - containerRect.left, y: e.clientY - containerRect.top });
    const node = nodes.find(n => n.id === nodeId);
    if (node) {
      const size = getNodeSize(node);
      setInitialSize(size);
    }
  };

  // ÂÅ¥ÈÇäÁ∑®ËºØÔºöÊõ¥Êñ∞Ë¢´ÈÅ∏ÁØÄÈªûÁöÑÂü∫Êú¨Â±¨ÊÄß
  const handleEditChange = (e) => {
    if (!selectedNodeId) return;
    const { name, value } = e.target;
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, [name]: value } : n
      )
    );
  };

  // ÁâπÂà•ËôïÁêÜ childrenÔºö‰ª•ÈÄóËôüÂàÜÈöîÁöÑÂ≠ó‰∏≤ËΩâÁÇ∫Èô£Âàó
  const handleChildrenChange = (e) => {
    if (!selectedNodeId) return;
    const children = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, children } : n
      )
    );
  };

  // Êñ∞Â¢ûÁØÄÈªû
  const handleAddNode = () => {
    const newNode = {
      id: `${Date.now()}`,
      type: "task", // È†êË®≠ÁÇ∫ taskÔºåÂèØÂæåÁ∫åË™øÊï¥
      label: "New Node",
      description: "",
      extras: [],
      link: "",
      children: [],
      position: { x: 50, y: 50 },
      ...DEFAULT_SIZE
    };
    setNodes(prev => [...prev, newNode]);
  };

  // ÂÑ≤Â≠òÊµÅÁ®ãÂúñÂà∞ÂæåÁ´Ø
  const handleSave = async () => {
    try {
      await axios.put(`${API_URL}/projects/${projectId}/flowchart`,
        { flowChart: nodes },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flowchart saved successfully!");
    } catch (error) {
      console.error("Failed to save flowchart:", error);
      alert("Save failed.");
    }
  };

  // Áï´ÈÄ£Á∑öÔºöÊ†πÊìöÁà∂ÁØÄÈªûÁöÑ childrenÔºåÂæûÁà∂ÁØÄÈªû‰∏≠ÂøÉÈÄ£Âà∞Â≠êÁØÄÈªû‰∏≠ÂøÉ
  const renderConnections = () => {
    const lines = [];
    nodes.forEach(parent => {
      if (parent.children && Array.isArray(parent.children)) {
        parent.children.forEach(childId => {
          const child = nodes.find(n => n.id === childId);
          if (child) {
            const parentSize = getNodeSize(parent);
            const childSize = getNodeSize(child);
            const startX = parent.position.x + parentSize.width / 2;
            const startY = parent.position.y + parentSize.height / 2;
            const endX = child.position.x + childSize.width / 2;
            const endY = child.position.y + childSize.height / 2;
            lines.push(
              <line
                key={`line-${parent.id}-${child.id}`}
                x1={startX}
                y1={startY}
                x2={endX}
                y2={endY}
                stroke="black"
                strokeWidth="2"
              />
            );
          }
        });
      }
    });
    return lines;
  };

  const selectedNode = nodes.find(n => n.id === selectedNodeId);

  return (
    <div>
      <h2>Custom Flow Chart Editor</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleSave} style={{ marginLeft: "10px" }}>Save Flow Chart</button>
      </div>
      <div
        ref={containerRef}
        style={{ position: "relative", width: "100%", height: "600px", border: "1px solid #ccc", overflow: "hidden" }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%" }}>
          {renderConnections()}
        </svg>
        {nodes.map(node => {
          const size = getNodeSize(node);
          return (
            <div
              key={node.id}
              style={{
                position: "absolute",
                left: node.position.x,
                top: node.position.y,
                width: size.width,
                height: size.height,
                boxSizing: "border-box",
                cursor: "move",
                border: selectedNode && selectedNode.id === node.id ? "2px solid blue" : "none"
              }}
              onMouseDown={(e) => handleMouseDown(e, node.id)}
              onClick={(e) => handleSelectNode(e, node.id)}
            >
              {renderNodeContent(node)}
              {/* Âà™Èô§ÊåâÈàï */}
              <button
                style={{
                  position: "absolute",
                  top: 0,
                  right: 0,
                  backgroundColor: "red",
                  color: "#fff",
                  border: "none",
                  cursor: "pointer",
                  fontSize: "10px",
                  padding: "2px"
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteNode(node.id);
                }}
              >
                X
              </button>
              {/* Ëã•Ë©≤ÁØÄÈªûË¢´ÈÅ∏‰∏≠ÔºåÈ°ØÁ§∫ resize handle */}
              {selectedNode && selectedNode.id === node.id && (
                <div
                  style={{
                    position: "absolute",
                    width: RESIZE_HANDLE_SIZE,
                    height: RESIZE_HANDLE_SIZE,
                    right: 0,
                    bottom: 0,
                    backgroundColor: "gray",
                    cursor: "nwse-resize"
                  }}
                  onMouseDown={(e) => handleResizeMouseDown(e, node.id)}
                />
              )}
            </div>
          );
        })}
      </div>
      {selectedNode && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h3>Edit Node Properties</h3>
          <div>
            <label>Type:</label>
            <select name="type" value={selectedNode.type} onChange={handleEditChange}>
              <option value="phase">phase</option>
              <option value="task">task</option>
              <option value="subFlow">subFlow</option>
              <option value="iterative">iterative</option>
              <option value="note">note</option>
              <option value="extra">extra</option>
            </select>
          </div>
          <div>
            <label>Label:</label>
            <input name="label" value={selectedNode.label} onChange={handleEditChange} />
          </div>
          <div>
            <label>Description:</label>
            <textarea name="description" value={selectedNode.description} onChange={handleEditChange} />
          </div>
          <div>
            <label>Link:</label>
            <input name="link" value={selectedNode.link} onChange={handleEditChange} />
          </div>
          <div>
            <label>Children (comma-separated IDs):</label>
            <input
              value={(selectedNode.children || []).join(",")}
              onChange={handleChildrenChange}
            />
          </div>
          <div>
            <label>Extras (comma-separated):</label>
            <input
              name="extras"
              value={(selectedNode.extras || []).join(",")}
              onChange={(e) => {
                const arr = e.target.value.split(",").map(s => s.trim()).filter(s => s);
                handleEditChange({ target: { name: "extras", value: arr } });
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomFlowChartEditor;
