// src/pages/HomePage.jsx
import React, { useState } from 'react';
import { Link, Route, Routes } from 'react-router-dom';
import Project from '../api/project'; // 假設你的 project.js 放在同一資料夾裡
// 你也可以 import 其他功能頁面，如 Mail, InternalFiles...

function HomePage() {
  const [showProjectManager, setShowProjectManager] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* 左側主選單 */}
      <div style={{ width: '200px', background: '#f5f5f5', padding: '20px' }}>
        <h3>YUNIVER</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li>
            <Link to="/mail">Mail</Link>
          </li>
          <li>
            <Link to="/internal-files">內部檔案</Link>
          </li>
          <li>
            <button onClick={() => setShowProjectManager(true)}>專案管理</button>
          </li>
          <li>
            <button onClick={handleLogout}>登出</button>
          </li>
        </ul>
      </div>

      {/* 右側主內容區 */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>首頁 (Dashboard)</h2>
        <p>這裡可以放一些系統總覽、公告、快速報表等。</p>

        {/* 
          如果直接想在此切換顯示「Project管理頁」，
          可以用 state 去條件渲染： 
        */}
        {showProjectManager && (
          <div>
            <hr />
            <Project />
          </div>
        )}

        {/* 
          若你想使用 react-router-dom 來做更清晰的分頁式瀏覽，
          可以這樣設定 Routes (但需在 App.js 裡設定 "/home/*" 之類的 nested route) 
        */}
        {/* 
        <Routes>
          <Route path="projects" element={<Project />} />
        </Routes>
        */}
      </div>
    </div>
  );
}

export default HomePage;
