import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

// 節點尺寸常數
const NODE_WIDTH = 140;
const NODE_HEIGHT = 80;

// 根據 type 產生不同的節點內容與樣式
const renderNodeContent = (node) => {
  const commonStyle = { padding: "4px", textAlign: "center" };
  switch(node.type) {
    case "phase":
      return (
        <div style={{ ...commonStyle, border: "2px dashed blue", backgroundColor: "#e0f7ff", height: "100%", display: "flex", flexDirection:"column", justifyContent:"center" }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label}</div>
          <div style={{ fontSize: "12px" }}>{node.description}</div>
        </div>
      );
    case "task":
      return (
        <div style={{ ...commonStyle, border: "2px solid black", backgroundColor: "#fff" }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label}</div>
          {node.important && <div style={{ color: "red", fontSize: "10px" }}>Important</div>}
          <div style={{ fontSize: "12px" }}>{node.description}</div>
        </div>
      );
    case "subFlow":
      return (
        <div style={{ ...commonStyle, border: "2px solid gray", backgroundColor: "#f0f0f0" }}>
          <div style={{ fontWeight: "bold", fontSize: "13px" }}>{node.label}</div>
          <div style={{ fontSize: "11px" }}>{node.description}</div>
        </div>
      );
    case "iterative":
      return (
        <div style={{ ...commonStyle, border: "2px dotted green", backgroundColor: "#e8ffe8" }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label} 🔄</div>
          <div style={{ fontSize: "12px" }}>{node.description}</div>
        </div>
      );
    case "note":
      return (
        <div style={{ ...commonStyle, border: "1px solid orange", backgroundColor: "#fff7d6" }}>
          <div style={{ fontStyle: "italic", fontSize: "12px" }}>{node.text || node.label}</div>
          <div style={{ fontSize: "10px" }}>{node.description}</div>
        </div>
      );
    case "extra":
      return (
        <div style={{ ...commonStyle, fontSize:"10px", padding: "2px", listStyle:"disc" }}>
          • {node.content}
        </div>
      );
    default:
      return (
        <div style={{ ...commonStyle, border: "1px solid black" }}>
          {node.label || "No Label"}
        </div>
      );
  }
};

const CustomFlowChartEditor = () => {
  const { id: projectId } = useParams();
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const containerRef = useRef(null);

  // 節點資料：每個物件至少包含 id, type, label, description, children, extras, link, position
  const [nodes, setNodes] = useState([]);
  // 儲存被選取編輯的節點 id
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  // 拖曳相關狀態
  const [draggingNodeId, setDraggingNodeId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // 讀取後端流程圖資料
  useEffect(() => {
    const fetchFlowChart = async () => {
      try {
        const res = await axios.get(`${API_URL}/projects/${projectId}/flowchart`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        // 預期 res.data 為一個物件陣列
        setNodes(res.data || []);
      } catch (error) {
        console.error("Failed to load flowchart:", error);
      }
    };
    fetchFlowChart();
  }, [projectId, API_URL, token]);

  // 拖曳節點：按下
  const handleMouseDown = (e, nodeId) => {
    e.stopPropagation();
    const nodeElem = e.currentTarget;
    const rect = nodeElem.getBoundingClientRect();
    setDraggingNodeId(nodeId);
    setDragOffset({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  // 拖曳：移動
  const handleMouseMove = (e) => {
    if (!draggingNodeId) return;
    const containerRect = containerRef.current.getBoundingClientRect();
    const newX = e.clientX - containerRect.left - dragOffset.x;
    const newY = e.clientY - containerRect.top - dragOffset.y;
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === draggingNodeId ? { ...n, position: { x: newX, y: newY } } : n
      )
    );
  };

  // 拖曳結束
  const handleMouseUp = () => {
    setDraggingNodeId(null);
  };

  // 節點選取（點選後用側邊表單編輯屬性）
  const handleSelectNode = (e, nodeId) => {
    e.stopPropagation();
    setSelectedNodeId(nodeId);
  };

  // 側邊編輯：更新被選節點屬性
  const handleEditChange = (e) => {
    if (!selectedNodeId) return;
    const { name, value } = e.target;
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, [name]: value } : n
      )
    );
  };

  // 特別處理 children（以逗號分隔的字串轉為陣列）
  const handleChildrenChange = (e) => {
    if (!selectedNodeId) return;
    const children = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, children } : n
      )
    );
  };

  // 新增節點：根據使用者需求，可預設 type 為 task
  const handleAddNode = () => {
    const newNode = {
      id: `${Date.now()}`,  // 使用 timestamp 當唯一 ID
      type: "task",         // 預設為 task，可後續修改
      label: "New Node",
      description: "",
      extras: [],
      link: "",
      children: [],
      position: { x: 50, y: 50 }
    };
    setNodes(prev => [...prev, newNode]);
  };

  // 儲存流程圖資料到後端
  const handleSave = async () => {
    try {
      await axios.put(`${API_URL}/projects/${projectId}/flowchart`,
        { flowChart: nodes },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flowchart saved successfully!");
    } catch (error) {
      console.error("Failed to save flowchart:", error);
      alert("Save failed.");
    }
  };

  // 依據每個節點的 position 畫出連線（簡單版：如果節點有 children，從 parent 連到 child）
  const renderConnections = () => {
    const lines = [];
    nodes.forEach(parent => {
      if (parent.children && Array.isArray(parent.children)) {
        parent.children.forEach(childId => {
          const child = nodes.find(n => n.id === childId);
          if (child) {
            const startX = parent.position.x + NODE_WIDTH / 2;
            const startY = parent.position.y + NODE_HEIGHT / 2;
            const endX = child.position.x + NODE_WIDTH / 2;
            const endY = child.position.y + NODE_HEIGHT / 2;
            lines.push(
              <line
                key={`line-${parent.id}-${child.id}`}
                x1={startX}
                y1={startY}
                x2={endX}
                y2={endY}
                stroke="black"
                strokeWidth="2"
              />
            );
          }
        });
      }
    });
    return lines;
  };

  // 取得被選中節點，供側邊表單編輯
  const selectedNode = nodes.find(n => n.id === selectedNodeId);

  return (
    <div>
      <h2>Custom Flow Chart Editor</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleSave} style={{ marginLeft: "10px" }}>Save Flow Chart</button>
      </div>
      <div
        ref={containerRef}
        style={{ position: "relative", width: "100%", height: "600px", border: "1px solid #ccc" }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%" }}>
          {renderConnections()}
        </svg>
        {nodes.map(node => (
          <div
            key={node.id}
            style={{
              position: "absolute",
              left: node.position.x,
              top: node.position.y,
              width: NODE_WIDTH,
              height: NODE_HEIGHT,
              cursor: "move",
              boxSizing: "border-box"
            }}
            onMouseDown={(e) => handleMouseDown(e, node.id)}
            onClick={(e) => handleSelectNode(e, node.id)}
          >
            {renderNodeContent(node)}
          </div>
        ))}
      </div>
      {/* 側邊編輯表單 */}
      {selectedNode && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h3>Edit Node Properties</h3>
          <div>
            <label>Type:</label>
            <select name="type" value={selectedNode.type} onChange={handleEditChange}>
              <option value="phase">phase</option>
              <option value="task">task</option>
              <option value="subFlow">subFlow</option>
              <option value="iterative">iterative</option>
              <option value="note">note</option>
              <option value="extra">extra</option>
            </select>
          </div>
          <div>
            <label>Label:</label>
            <input name="label" value={selectedNode.label} onChange={handleEditChange} />
          </div>
          <div>
            <label>Description:</label>
            <textarea name="description" value={selectedNode.description} onChange={handleEditChange} />
          </div>
          <div>
            <label>Link:</label>
            <input name="link" value={selectedNode.link} onChange={handleEditChange} />
          </div>
          <div>
            <label>Children (comma-separated IDs):</label>
            <input
              value={(selectedNode.children || []).join(",")}
              onChange={handleChildrenChange}
            />
          </div>
          <div>
            <label>Extras (comma-separated):</label>
            <input
              name="extras"
              value={(selectedNode.extras || []).join(",")}
              onChange={(e) => {
                const arr = e.target.value.split(",").map(s => s.trim()).filter(s => s);
                handleEditChange({ target: { name: "extras", value: arr } });
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomFlowChartEditor;
