import React, { useEffect, useState } from 'react';
import {
  fetchAllProjects,
  createProject,
  updateProject,
  deleteProject,
} from '../api/project';

function ProjectManager() {
  const [projects, setProjects] = useState([]);
  const [editProjectId, setEditProjectId] = useState(null);
  const [formData, setFormData] = useState({
    clientName: '',
    projectName: '',
    owner: '',  // 如果需要指派給 userId
    startDate: '',
    endDate: '',
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    try {
      const data = await fetchAllProjects(token);
      setProjects(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      clientName: formData.clientName,
      projectName: formData.projectName,
      owner: formData.owner,
      period: {
        startDate: formData.startDate,
        endDate: formData.endDate,
      },
      // 其他欄位、tasks...
    };

    try {
      if (editProjectId) {
        // 更新
        await updateProject(editProjectId, payload, token);
        alert('專案更新成功');
      } else {
        // 新建
        await createProject(payload, token);
        alert('專案建立成功');
      }
      setFormData({
        clientName: '',
        projectName: '',
        owner: '',
        startDate: '',
        endDate: '',
      });
      setEditProjectId(null);
      loadProjects();
    } catch (err) {
      console.error('Create/Update Project Error', err);
      alert('操作失敗');
    }
  };

  const handleEdit = (project) => {
    setEditProjectId(project._id);
    setFormData({
      clientName: project.clientName,
      projectName: project.projectName,
      owner: project.owner?._id || '',
      startDate: project.period?.startDate?.slice(0, 10) || '',
      endDate: project.period?.endDate?.slice(0, 10) || '',
    });
  };

  const handleDelete = async (id) => {
    if (!window.confirm('確定要刪除？')) return;
    try {
      await deleteProject(id, token);
      alert('刪除成功');
      loadProjects();
    } catch (err) {
      console.error(err);
      alert('刪除失敗');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>專案管理 (CRUD)</h2>

      {/* 專案清單 */}
      <table border="1" cellPadding="8" style={{ marginTop: '20px' }}>
        <thead>
          <tr>
            <th>客戶名稱</th>
            <th>專案名稱</th>
            <th>負責人</th>
            <th>開始 ~ 結束</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((proj) => (
            <tr key={proj._id}>
              <td>{proj.clientName}</td>
              <td>{proj.projectName}</td>
              <td>{proj.owner?.username || 'N/A'}</td>
              <td>
                {proj.period?.startDate
                  ? new Date(proj.period.startDate).toLocaleDateString()
                  : ''}{' '}
                ~{' '}
                {proj.period?.endDate
                  ? new Date(proj.period.endDate).toLocaleDateString()
                  : ''}
              </td>
              <td>
                <button onClick={() => handleEdit(proj)}>編輯</button>{' '}
                <button onClick={() => handleDelete(proj._id)}>刪除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <hr />

      {/* 新增 / 編輯 表單 */}
      <h3>{editProjectId ? '編輯專案' : '新增專案'}</h3>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>客戶名稱: </label>
          <input
            value={formData.clientName}
            onChange={(e) => setFormData({ ...formData, clientName: e.target.value })}
            required
          />
        </div>
        <div>
          <label>專案名稱: </label>
          <input
            value={formData.projectName}
            onChange={(e) => setFormData({ ...formData, projectName: e.target.value })}
            required
          />
        </div>
        <div>
          <label>負責人(Owner ID): </label>
          <input
            value={formData.owner}
            onChange={(e) => setFormData({ ...formData, owner: e.target.value })}
            placeholder="若要指定成員ID"
          />
        </div>
        <div>
          <label>開始日期: </label>
          <input
            type="date"
            value={formData.startDate}
            onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
          />
        </div>
        <div>
          <label>結束日期: </label>
          <input
            type="date"
            value={formData.endDate}
            onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
          />
        </div>
        <button type="submit" style={{ marginTop: '10px' }}>
          {editProjectId ? '更新' : '新增'}
        </button>
      </form>
    </div>
  );
}

export default ProjectManager;
