// src/pages/CustomFlowChartEditor.jsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const DEFAULT_NODE_SIZE = { width: 140, height: 80 };
const DEFAULT_PHASE_SIZE = { width: 150, height: 100 };

const getDefaultSize = (node) => {
  return node.type === 'phase' ? DEFAULT_PHASE_SIZE : DEFAULT_NODE_SIZE;
};

const renderNodeContent = (node) => {
  const commonStyle = { padding: "4px", textAlign: "center", fontSize: "12px" };
  switch(node.type) {
    case "phase":
      return (
        <div style={{
          ...commonStyle,
          border: "2px dashed blue",
          backgroundColor: "#e0f7ff",
          height: "100%",
          display: "flex",
          flexDirection:"column",
          justifyContent:"center"
        }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "task":
      return (
        <div style={{
          ...commonStyle,
          border: "2px solid black",
          backgroundColor: "#fff"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          {node.important && <div style={{ color: "red" }}>Important</div>}
          <div>{node.description}</div>
        </div>
      );
    case "subFlow":
      return (
        <div style={{
          ...commonStyle,
          border: "2px solid gray",
          backgroundColor: "#f0f0f0"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "iterative":
      return (
        <div style={{
          ...commonStyle,
          border: "2px dotted green",
          backgroundColor: "#e8ffe8"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label} üîÑ</div>
          <div>{node.description}</div>
        </div>
      );
    case "note":
      return (
        <div style={{
          ...commonStyle,
          border: "1px solid orange",
          backgroundColor: "#fff7d6"
        }}>
          <div style={{ fontStyle: "italic" }}>{node.text || node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "extra":
      return (
        <div style={{ ...commonStyle, fontSize:"10px" }}>
          ‚Ä¢ {node.content}
        </div>
      );
    default:
      return (
        <div style={{ ...commonStyle, border: "1px solid black" }}>
          {node.label || "No Label"}
        </div>
      );
  }
};

const CustomFlowChartEditor = () => {
  const { id: projectId } = useParams();
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const containerRef = useRef(null);

  // nodes: ÊØèÂÄãÁØÄÈªûÂåÖÂê´ id, type, label, description, link, extras, children (array), position, size
  const [nodes, setNodes] = useState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [draggingNodeId, setDraggingNodeId] = useState(null);
  const [resizingNodeId, setResizingNodeId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [resizeStart, setResizeStart] = useState({ x: 0, y: 0, width: 0, height: 0 });

  useEffect(() => {
    const fetchFlowChart = async () => {
      try {
        const res = await axios.get(`${API_URL}/projects/${projectId}/flowchart`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = res.data || [];
        // Á¢∫‰øùÊØèÂÄãÁØÄÈªûÊúâ size Ê¨Ñ‰Ωç
        const enriched = data.map(node => ({
          ...node,
          size: node.size || getDefaultSize(node)
        }));
        setNodes(enriched);
      } catch (error) {
        console.error("Failed to load flowchart:", error);
      }
    };
    fetchFlowChart();
  }, [projectId, API_URL, token]);

  // ÊãñÊõ≥ÁØÄÈªûÔºöÈñãÂßãÊãñÂãïÊôÇË®òÈåÑÂÅèÁßª
  const handleMouseDown = (e, nodeId) => {
    e.stopPropagation();
    const nodeElem = e.currentTarget;
    const rect = nodeElem.getBoundingClientRect();
    setDraggingNodeId(nodeId);
    setDragOffset({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  const handleMouseMove = (e) => {
    if (draggingNodeId) {
      const containerRect = containerRef.current.getBoundingClientRect();
      const newX = e.clientX - containerRect.left - dragOffset.x;
      const newY = e.clientY - containerRect.top - dragOffset.y;
      setNodes(prevNodes => prevNodes.map(n => {
        if (n.id === draggingNodeId) {
          const deltaX = newX - n.position.x;
          const deltaY = newY - n.position.y;
          // Ëã•Ê≠§ÁØÄÈªûÁÇ∫ phaseÔºåÂâáÂêåÊôÇÁßªÂãïÊâÄÊúâÂÖ∂ children ÁØÄÈªû
          if (n.type === 'phase' && n.children && n.children.length > 0) {
            return { ...n, position: { x: newX, y: newY } };
          }
          return { ...n, position: { x: newX, y: newY } };
        }
        // Ëã•Ê≠§ÁØÄÈªûÂ±¨ÊñºÊüêÂÄã phaseÊ≠£Âú®Ë¢´ÊãñÂãïÔºåÂâá‰∏Ä‰ΩµÊõ¥Êñ∞‰ΩçÁΩÆ
        const parent = prevNodes.find(p => p.type === 'phase' && p.children && p.children.includes(draggingNodeId));
        if (parent && n.id === draggingNodeId) {
          // ÈÄôË£°ÊàëÂÄëÂè™ÊãñÂãï phase Ëá™Ë∫´
          return n;
        }
        return n;
      }));
    }
  };

  const handleMouseUp = () => {
    // Ê™¢Êü•Â¶ÇÊûú‰∏ÄÂÄãÈùû phase ÁØÄÈªûË¢´ÊãñÂÖ• phase ÁØÄÈªûÂçÄÂüüÔºåÂâáÂä†ÂÖ• phase ÁöÑ children
    if (draggingNodeId) {
      const draggedNode = nodes.find(n => n.id === draggingNodeId);
      if (draggedNode) {
        const draggedCenter = {
          x: draggedNode.position.x + draggedNode.size.width / 2,
          y: draggedNode.position.y + draggedNode.size.height / 2,
        };
        setNodes(prevNodes =>
          prevNodes.map(n => {
            if (n.type === 'phase' && n.id !== draggingNodeId) {
              const { x, y } = n.position;
              const { width, height } = n.size;
              if (
                draggedCenter.x >= x &&
                draggedCenter.x <= x + width &&
                draggedCenter.y >= y &&
                draggedCenter.y <= y + height
              ) {
                // Â∞á draggedNode Âä†ÂÖ• phase ÁöÑ childrenÔºàÈÅøÂÖçÈáçË§áÔºâ
                const children = n.children ? [...n.children] : [];
                if (!children.includes(draggingNodeId)) {
                  children.push(draggingNodeId);
                }
                return { ...n, children };
              }
            }
            return n;
          })
        );
      }
    }
    setDraggingNodeId(null);
  };

  // Ë™øÊï¥Â§ßÂ∞èÔºöÂè≥‰∏ãËßíÊâãÊüÑÁöÑÊãñÊõ≥
  const handleResizeMouseDown = (e, nodeId) => {
    e.stopPropagation();
    setResizingNodeId(nodeId);
    const node = nodes.find(n => n.id === nodeId);
    setResizeStart({ x: e.clientX, y: e.clientY, width: node.size.width, height: node.size.height });
  };

  const handleResizeMouseMove = (e) => {
    if (!resizingNodeId) return;
    const deltaX = e.clientX - resizeStart.x;
    const deltaY = e.clientY - resizeStart.y;
    setNodes(prevNodes => prevNodes.map(n => {
      if (n.id === resizingNodeId) {
        return {
          ...n,
          size: {
            width: Math.max(50, resizeStart.width + deltaX),
            height: Math.max(30, resizeStart.height + deltaY)
          }
        };
      }
      return n;
    }));
  };

  const handleResizeMouseUp = () => {
    setResizingNodeId(null);
  };

  // ÁØÄÈªûÈÅ∏Âèñ
  const handleSelectNode = (e, nodeId) => {
    e.stopPropagation();
    setSelectedNodeId(nodeId);
  };

  // Âà™Èô§ÁØÄÈªûÔºàÂêåÊôÇÂæûÊâÄÊúâÁà∂ÁØÄÈªû children ÁßªÈô§Ôºâ
  const handleDeleteNode = (nodeId) => {
    setNodes(prevNodes => prevNodes
      .filter(n => n.id !== nodeId)
      .map(n => n.children ? { ...n, children: n.children.filter(cid => cid !== nodeId) } : n)
    );
    if (selectedNodeId === nodeId) setSelectedNodeId(null);
  };

  // ÂÅ¥ÈÇäË°®ÂñÆÊõ¥Êñ∞ÔºàÁõ¥Êé•Â±¨ÊÄßÔºâ
  const handleEditChange = (e) => {
    if (!selectedNodeId) return;
    const { name, value } = e.target;
    setNodes(prevNodes => prevNodes.map(n =>
      n.id === selectedNodeId ? { ...n, [name]: value } : n
    ));
  };

  const handleChildrenChange = (e) => {
    if (!selectedNodeId) return;
    const children = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prevNodes => prevNodes.map(n =>
      n.id === selectedNodeId ? { ...n, children } : n
    ));
  };

  // Êñ∞Â¢ûÁØÄÈªû
  const handleAddNode = () => {
    const newNode = {
      id: `${Date.now()}`,
      type: "task", // È†êË®≠ type ÁÇ∫ task
      label: "New Node",
      description: "",
      extras: [],
      link: "",
      children: [],
      position: { x: 50, y: 50 },
      size: DEFAULT_NODE_SIZE
    };
    setNodes(prev => [...prev, newNode]);
  };

  // ÂÑ≤Â≠òÊµÅÁ®ãÂúñÂà∞ÂæåÁ´Ø
  const handleSave = async () => {
    try {
      await axios.put(`${API_URL}/projects/${projectId}/flowchart`,
        { flowChart: nodes },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flowchart saved successfully!");
    } catch (error) {
      console.error("Failed to save flowchart:", error);
      alert("Save failed.");
    }
  };

  // ‰ΩøÁî® SVG Áï´ÈÄ£Á∑öÔºöÂæûÊØèÂÄã phase ÁöÑ‰∏≠ÂøÉÂà∞ÂÖ∂Â≠êÁØÄÈªû‰∏≠ÂøÉ
  const renderConnections = () => {
    const lines = [];
    nodes.forEach(parent => {
      if (parent.children && Array.isArray(parent.children)) {
        parent.children.forEach(childId => {
          const child = nodes.find(n => n.id === childId);
          if (child) {
            const pDims = parent.size;
            const cDims = child.size;
            const startX = parent.position.x + pDims.width / 2;
            const startY = parent.position.y + pDims.height / 2;
            const endX = child.position.x + cDims.width / 2;
            const endY = child.position.y + cDims.height / 2;
            lines.push(
              <line
                key={`line-${parent.id}-${child.id}`}
                x1={startX}
                y1={startY}
                x2={endX}
                y2={endY}
                stroke="black"
                strokeWidth="2"
              />
            );
          }
        });
      }
    });
    return lines;
  };

  const selectedNode = nodes.find(n => n.id === selectedNodeId);

  return (
    <div>
      <h2>Custom Flow Chart Editor</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleSave} style={{ marginLeft: "10px" }}>Save Flow Chart</button>
      </div>
      <div
        ref={containerRef}
        style={{ position: "relative", width: "100%", height: "600px", border: "1px solid #ccc" }}
        onMouseMove={(e) => {
          handleMouseMove(e);
          handleResizeMouseMove(e);
        }}
        onMouseUp={() => {
          handleMouseUp();
          handleResizeMouseUp();
        }}
      >
        <svg style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%" }}>
          {renderConnections()}
        </svg>
        {nodes.map(node => (
          <div
            key={node.id}
            style={{
              position: "absolute",
              left: node.position.x,
              top: node.position.y,
              width: node.size.width,
              height: node.size.height,
              boxSizing: "border-box",
              cursor: "move",
              border: selectedNodeId === node.id ? "2px solid blue" : "1px solid #000",
              backgroundColor: "#fff"
            }}
            onMouseDown={(e) => handleMouseDown(e, node.id)}
            onClick={(e) => handleSelectNode(e, node.id)}
          >
            {renderNodeContent(node)}
            <button
              style={{
                position: "absolute",
                top: 0,
                right: 0,
                backgroundColor: "red",
                color: "#fff",
                border: "none",
                cursor: "pointer",
                fontSize: "10px",
                padding: "2px"
              }}
              onClick={(e) => {
                e.stopPropagation();
                handleDeleteNode(node.id);
              }}
            >
              X
            </button>
            {/* È°ØÁ§∫Ë™øÊï¥Â§ßÂ∞èÊâãÊüÑ */}
            {selectedNodeId === node.id && (
              <div
                style={{
                  position: "absolute",
                  right: 0,
                  bottom: 0,
                  width: "10px",
                  height: "10px",
                  backgroundColor: "gray",
                  cursor: "nwse-resize"
                }}
                onMouseDown={(e) => handleResizeMouseDown(e, node.id)}
              ></div>
            )}
          </div>
        ))}
      </div>
      {selectedNode && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h3>Edit Node Properties</h3>
          <div>
            <label>Type:</label>
            <select name="type" value={selectedNode.type} onChange={handleEditChange}>
              <option value="phase">phase</option>
              <option value="task">task</option>
              <option value="subFlow">subFlow</option>
              <option value="iterative">iterative</option>
              <option value="note">note</option>
              <option value="extra">extra</option>
            </select>
          </div>
          <div>
            <label>Label:</label>
            <input name="label" value={selectedNode.label} onChange={handleEditChange} />
          </div>
          <div>
            <label>Description:</label>
            <textarea name="description" value={selectedNode.description} onChange={handleEditChange} />
          </div>
          <div>
            <label>Link:</label>
            <input name="link" value={selectedNode.link} onChange={handleEditChange} />
          </div>
          <div>
            <label>Children (comma-separated IDs):</label>
            <input
              value={(selectedNode.children || []).join(",")}
              onChange={handleChildrenChange}
            />
          </div>
          <div>
            <label>Extras (comma-separated):</label>
            <input
              name="extras"
              value={(selectedNode.extras || []).join(",")}
              onChange={(e) => {
                const arr = e.target.value.split(",").map(s => s.trim()).filter(s => s);
                handleEditChange({ target: { name: "extras", value: arr } });
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomFlowChartEditor;
