// src/pages/project.js
import React, { useState, useEffect } from 'react';
import {
  fetchAllProjects,
  createProject,
  updateProject,
} from './project';

function Project() {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({
    clientName: '',
    projectName: '',
    owner: '',
    startDate: '',
    endDate: '',
    progress: 0, // 0 ~ 100
  });
  const [editProjectId, setEditProjectId] = useState(null);

  const token = localStorage.getItem('token'); // 從 localStorage 取得登入後的 token

  // 一進到此頁面，就抓所有專案
  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    try {
      const data = await fetchAllProjects(token);
      setProjects(data);
    } catch (error) {
      console.error('Failed to fetch projects:', error);
      alert('取得專案失敗');
    }
  };

  // 新增或更新專案
  const handleSubmit = async (e) => {
    e.preventDefault();

    // 將時間字串轉成 Date 物件 (如果後端需要)
    const payload = {
      clientName: newProject.clientName,
      projectName: newProject.projectName,
      owner: newProject.owner, // 這裡可以是 userId，實際上後端會對應該使用者
      period: {
        startDate: newProject.startDate,
        endDate: newProject.endDate,
      },
      // 進度可用 tasks 或 progress，這邊示範用 progress 數字來代表
      tasks: [
        {
          name: '設計階段',
          status: newProject.progress < 100 ? 'in-progress' : 'done',
        },
      ],
    };

    try {
      if (!editProjectId) {
        // 如果沒有 editProjectId，代表是「新建」
        await createProject(payload, token);
        alert('專案新增成功');
      } else {
        // 如果有 editProjectId，代表是「編輯現有專案」
        await updateProject(editProjectId, payload, token);
        alert('專案更新成功');
      }
      // 重新抓取專案列表
      loadProjects();
      // 清空表單
      setNewProject({
        clientName: '',
        projectName: '',
        owner: '',
        startDate: '',
        endDate: '',
        progress: 0,
      });
      setEditProjectId(null);
    } catch (error) {
      console.error('Error in create/update project:', error);
      alert('操作失敗');
    }
  };

  // 編輯按鈕：填入表單
  const handleEdit = (project) => {
    setEditProjectId(project._id);
    setNewProject({
      clientName: project.clientName,
      projectName: project.projectName,
      owner: project.owner?._id || '', // 可能要看後端回傳的結構
      startDate: project.period?.startDate?.slice(0, 10) || '',
      endDate: project.period?.endDate?.slice(0, 10) || '',
      // 假設你用 tasks 中第一個階段的狀態來推測 progress
      progress: project.tasks && project.tasks.length
        ? (project.tasks[0].status === 'done' ? 100 : 50) 
        : 0,
    });
  };

  // 簡易進度條顯示 (0~100)
  const renderProgressBar = (progress) => {
    const barStyle = {
      width: `${progress}%`,
      backgroundColor: '#4caf50',
      height: '20px',
    };
    return (
      <div style={{ border: '1px solid #ccc', width: '100px', margin: 'auto' }}>
        <div style={barStyle}></div>
      </div>
    );
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>專案管理</h2>

      {/* 專案列表 */}
      <table border="1" cellPadding="8" style={{ marginBottom: '20px' }}>
        <thead>
          <tr>
            <th>客戶名稱</th>
            <th>專案名稱</th>
            <th>負責人</th>
            <th>期間</th>
            <th>進度</th>
            <th>編輯</th>
          </tr>
        </thead>
        <tbody>
          {projects.map((proj) => {
            // 以 proj.tasks[] 來簡化判斷進度
            const progressValue = proj.tasks && proj.tasks.length
              ? (proj.tasks[0].status === 'done' ? 100 : 50)
              : 0;
            return (
              <tr key={proj._id}>
                <td>{proj.clientName}</td>
                <td>{proj.projectName}</td>
                <td>{proj.owner?.username || 'N/A'}</td>
                <td>
                  {proj.period?.startDate
                    ? new Date(proj.period.startDate).toLocaleDateString()
                    : ''} ~
                  {proj.period?.endDate
                    ? new Date(proj.period.endDate).toLocaleDateString()
                    : ''}
                </td>
                <td>{renderProgressBar(progressValue)}</td>
                <td>
                  <button onClick={() => handleEdit(proj)}>編輯</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* 新增 / 編輯 專案表單 */}
      <h3>{editProjectId ? '編輯專案' : '新增專案'}</h3>
      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <div>
          <label>客戶名稱: </label>
          <input
            type="text"
            value={newProject.clientName}
            onChange={(e) =>
              setNewProject({ ...newProject, clientName: e.target.value })
            }
            required
          />
        </div>
        <div>
          <label>專案名稱: </label>
          <input
            type="text"
            value={newProject.projectName}
            onChange={(e) =>
              setNewProject({ ...newProject, projectName: e.target.value })
            }
            required
          />
        </div>
        <div>
          <label>負責人 (owner userId 或名稱): </label>
          <input
            type="text"
            value={newProject.owner}
            onChange={(e) =>
              setNewProject({ ...newProject, owner: e.target.value })
            }
          />
        </div>
        <div>
          <label>開始日期: </label>
          <input
            type="date"
            value={newProject.startDate}
            onChange={(e) =>
              setNewProject({ ...newProject, startDate: e.target.value })
            }
          />
        </div>
        <div>
          <label>結束日期: </label>
          <input
            type="date"
            value={newProject.endDate}
            onChange={(e) =>
              setNewProject({ ...newProject, endDate: e.target.value })
            }
          />
        </div>
        <div>
          <label>進度 (0-100): </label>
          <input
            type="number"
            value={newProject.progress}
            onChange={(e) =>
              setNewProject({ ...newProject, progress: Number(e.target.value) })
            }
            min={0}
            max={100}
          />
        </div>
        <button type="submit">{editProjectId ? '更新' : '新增'}</button>
      </form>
    </div>
  );
}

export default Project;
