// src/pages/ProjectDetails.jsx
import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getProjectDetails, addNote, addMessage } from '../api/project';

function ProjectDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [project, setProject] = useState(null);
  const [noteContent, setNoteContent] = useState('');
  const [messageContent, setMessageContent] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [showNotes, setShowNotes] = useState(true);
  const [showMessages, setShowMessages] = useState(true);

  useEffect(() => {
    loadProject();
    // eslint-disable-next-line
  }, [id]);

  const loadProject = async () => {
    try {
      const data = await getProjectDetails(id, token);
      // 如果後端未提供 pages 或 files，可以設定預設空陣列
      setProject({
        ...data,
        pages: data.pages || [],
        files: data.files || []
      });
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddNote = async () => {
    try {
      await addNote(id, noteContent, token);
      alert('備註已新增');
      setNoteContent('');
      loadProject();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddMessage = async () => {
    try {
      await addMessage(id, messageContent, token);
      alert('訊息已新增');
      setMessageContent('');
      loadProject();
    } catch (err) {
      console.error(err);
    }
  };

  // 檔案上傳：這裡僅模擬上傳（實際上可呼叫後端 API）
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    // 模擬檔案資料：實際上可利用 FormData 並呼叫 API 上傳
    const newFile = {
      id: Date.now(),
      name: file.name,
      url: URL.createObjectURL(file)
    };
    setProject(prev => ({
      ...prev,
      files: prev.files ? [...prev.files, newFile] : [newFile]
    }));
  };

  // 檔案預覽：點擊檔案後更新選取檔案狀態
  const handleSelectFile = (file) => {
    setSelectedFile(file);
  };

  if (!project) return <div style={{ padding: '20px' }}>Loading...</div>;

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* 左側側邊欄 */}
      <div style={{ width: '250px', borderRight: '1px solid #ccc', padding: '10px', display: 'flex', flexDirection: 'column' }}>
        {/* 上半：專案基本資訊 */}
        <div style={{ marginBottom: '20px' }}>
          <h2>專案資訊</h2>
          <p><strong>客戶名稱：</strong>{project.clientName}</p>
          <p><strong>專案名稱：</strong>{project.projectName}</p>
          <p><strong>負責人：</strong>{project.owner?.username || 'N/A'}</p>
          <p>
            <strong>期間：</strong>
            {project.period?.startDate ? new Date(project.period.startDate).toLocaleDateString() : ''} ~ 
            {project.period?.endDate ? new Date(project.period.endDate).toLocaleDateString() : ''}
          </p>
        </div>
        {/* 中間：頁面列表 */}
        <div style={{ flexGrow: 1, overflowY: 'auto' }}>
          <h3>頁面列表</h3>
          {project.pages.length > 0 ? (
            <ul>
              {project.pages.map(page => (
                <li key={page.id} style={{ cursor: 'pointer' }}>
                  {page.name}
                </li>
              ))}
            </ul>
          ) : (
            <p>尚未建立頁面</p>
          )}
          <button style={{ marginTop: '10px' }}>新增頁面</button>
        </div>
        {/* 下半：按鈕 */}
        <div style={{ marginTop: '20px' }}>
          <button onClick={() => navigate(`/flowchart-editor/${id}`)}>案件流程圖</button>
          <button onClick={() => navigate(`/project-settings/${id}`)} style={{ marginLeft: '10px' }}>案件資訊設定</button>
        </div>
      </div>

      {/* 中間區域：檔案列表與上傳功能 */}
      <div style={{ width: '300px', borderRight: '1px solid #ccc', padding: '10px', overflowY: 'auto' }}>
        <h3>檔案列表</h3>
        {project.files.length > 0 ? (
          <ul>
            {project.files.map(file => (
              <li key={file.id} style={{ cursor: 'pointer' }} onClick={() => handleSelectFile(file)}>
                {file.name}
              </li>
            ))}
          </ul>
        ) : (
          <p>尚無檔案</p>
        )}
        <div style={{ marginTop: '10px' }}>
          <input type="file" onChange={handleFileUpload} />
        </div>
      </div>

      {/* 右側區域：檔案預覽 */}
      <div style={{ flexGrow: 1, padding: '10px' }}>
        <h3>檔案預覽</h3>
        {selectedFile ? (
          <div>
            <iframe src={selectedFile.url} title={selectedFile.name} style={{ width: '100%', height: '80%' }}></iframe>
            <a href={selectedFile.url} download={selectedFile.name}>
              <button>下載檔案</button>
            </a>
          </div>
        ) : (
          <p>請選擇檔案進行預覽</p>
        )}
      </div>

      {/* 備註與溝通訊息懸浮視窗 */}
      {showNotes && (
        <div style={{
          position: 'fixed', bottom: '20px', left: '260px', width: '300px', height: '200px',
          border: '1px solid #ccc', backgroundColor: '#fff', padding: '10px', overflowY: 'auto'
        }}>
          <h4>備註 (Notes)</h4>
          <ul>
            {project.notes?.map(note => (
              <li key={note._id}>
                {note.content} <small>({new Date(note.createdAt).toLocaleString()})</small>
              </li>
            ))}
          </ul>
          <input
            placeholder="輸入備註"
            value={noteContent}
            onChange={(e) => setNoteContent(e.target.value)}
          />
          <button onClick={handleAddNote}>新增備註</button>
          <button onClick={() => setShowNotes(false)} style={{ float: 'right' }}>隱藏</button>
        </div>
      )}
      {showMessages && (
        <div style={{
          position: 'fixed', bottom: '240px', left: '260px', width: '300px', height: '200px',
          border: '1px solid #ccc', backgroundColor: '#fff', padding: '10px', overflowY: 'auto'
        }}>
          <h4>溝通記錄 (Messages)</h4>
          <ul>
            {project.messages?.map(msg => (
              <li key={msg._id}>
                {msg.message} <small>({new Date(msg.createdAt).toLocaleString()})</small>
              </li>
            ))}
          </ul>
          <input
            placeholder="輸入訊息"
            value={messageContent}
            onChange={(e) => setMessageContent(e.target.value)}
          />
          <button onClick={handleAddMessage}>新增訊息</button>
          <button onClick={() => setShowMessages(false)} style={{ float: 'right' }}>隱藏</button>
        </div>
      )}
    </div>
  );
}

export default ProjectDetails;
