// src/pages/CustomFlowChartEditor.jsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const NODE_WIDTH_PHASE = 300;
const NODE_HEIGHT_PHASE = 200;
const NODE_WIDTH_DEFAULT = 120;
const NODE_HEIGHT_DEFAULT = 60;

// 自定義節點元件，根據 node.type 呈現不同樣式
const NodeComponent = ({ node, onMouseDown, onClick, onDrop, onDragOver }) => {
  const style = {
    position: "absolute",
    left: node.position.x,
    top: node.position.y,
    width: node.type === "phase" ? NODE_WIDTH_PHASE : NODE_WIDTH_DEFAULT,
    height: node.type === "phase" ? NODE_HEIGHT_PHASE : NODE_HEIGHT_DEFAULT,
    padding: "4px",
    boxSizing: "border-box",
    cursor: "move",
    userSelect: "none",
    backgroundColor: "#fff",
    border:
      node.type === "phase"
        ? "2px dashed blue"
        : node.type === "task"
        ? "1px solid green"
        : node.type === "subFlow"
        ? "1px solid orange"
        : node.type === "iterative"
        ? "2px solid red"
        : node.type === "note"
        ? "1px solid gray"
        : node.type === "extra"
        ? "1px dotted purple"
        : "1px solid black",
  };

  return (
    <div
      draggable
      onMouseDown={(e) => onMouseDown(e, node.id)}
      onClick={(e) => onClick(e, node.id)}
      onDrop={(e) => onDrop(e, node.id)}
      onDragOver={(e) => onDragOver(e)}
      style={style}
    >
      <strong>{node.label}</strong>
      <div style={{ fontSize: "12px" }}>{node.type}</div>
    </div>
  );
};

const CustomFlowChartEditor = () => {
  const { id: projectId } = useParams();
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const containerRef = useRef(null);

  const [nodes, setNodes] = useState([]);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [draggingNodeId, setDraggingNodeId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // 從後端讀取流程圖 JSON
  useEffect(() => {
    const fetchFlowChart = async () => {
      try {
        const res = await axios.get(`${API_URL}/projects/${projectId}/flowchart`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNodes(res.data || []);
      } catch (error) {
        console.error("Failed to load flowchart:", error);
      }
    };
    fetchFlowChart();
  }, [projectId, API_URL, token]);

  // 開始拖動：記錄被拖曳的節點與滑鼠點選時的 offset
  const handleMouseDown = (e, nodeId) => {
    e.stopPropagation();
    const node = nodes.find(n => n.id === nodeId);
    if (!node) return;
    setDraggingNodeId(nodeId);
    const rect = e.currentTarget.getBoundingClientRect();
    setDragOffset({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  // 拖曳過程中更新節點位置
  const handleMouseMove = (e) => {
    if (!draggingNodeId) return;
    const containerRect = containerRef.current.getBoundingClientRect();
    const newX = e.clientX - containerRect.left - dragOffset.x;
    const newY = e.clientY - containerRect.top - dragOffset.y;
    setNodes(prev =>
      prev.map(n => n.id === draggingNodeId ? { ...n, position: { x: newX, y: newY } } : n)
    );
  };

  // 結束拖曳
  const handleMouseUp = () => {
    setDraggingNodeId(null);
  };

  // 點擊節點選擇以編輯屬性
  const handleClick = (e, nodeId) => {
    e.stopPropagation();
    setSelectedNodeId(nodeId);
  };

  // 當拖曳節點落在另一個節點上時，若目標為 phase 節點，則加入拖曳節點作為其子節點
  const handleDrop = (e, targetNodeId) => {
    e.preventDefault();
    e.stopPropagation();
    const targetNode = nodes.find(n => n.id === targetNodeId);
    if (targetNode && targetNode.type === "phase" && draggingNodeId && draggingNodeId !== targetNodeId) {
      setNodes(prev =>
        prev.map(n => {
          if (n.id === targetNodeId) {
            const children = n.children || [];
            if (!children.includes(draggingNodeId)) {
              return { ...n, children: [...children, draggingNodeId] };
            }
          }
          return n;
        })
      );
    }
    setDraggingNodeId(null);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  // 儲存流程圖：將 nodes JSON 傳到後端
  const handleSave = async () => {
    try {
      await axios.put(`${API_URL}/projects/${projectId}/flowchart`,
        { flowChart: nodes },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flowchart saved!");
    } catch (error) {
      console.error("Failed to save flowchart:", error);
      alert("Save failed.");
    }
  };

  // 新增節點
  const handleAddNode = () => {
    const newNode = {
      id: `${Date.now()}`, // 使用 timestamp 作為簡單 ID
      type: "task", // 預設為 task，可從編輯表單更改
      label: "New Node",
      description: "",
      extras: [],
      link: "",
      children: [],
      position: { x: 50, y: 50 }
    };
    setNodes(prev => [...prev, newNode]);
  };

  // 取得選中節點
  const selectedNode = nodes.find(n => n.id === selectedNodeId);

  // 側邊編輯表單變更處理（直接更新選中節點的屬性）
  const handleEditChange = (e) => {
    if (!selectedNode) return;
    const { name, value } = e.target;
    setNodes(prev => prev.map(n =>
      n.id === selectedNodeId ? { ...n, [name]: value } : n
    ));
  };

  // 對 extras 與 children 作特殊處理
  const handleExtrasChange = (e) => {
    if (!selectedNode) return;
    const extras = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prev => prev.map(n =>
      n.id === selectedNodeId ? { ...n, extras } : n
    ));
  };

  const handleChildrenChange = (e) => {
    if (!selectedNode) return;
    const children = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prev => prev.map(n =>
      n.id === selectedNodeId ? { ...n, children } : n
    ));
  };

  return (
    <div>
      <h2>Custom Flow Chart Editor</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleSave} style={{ marginLeft: "10px" }}>Save Flow Chart</button>
      </div>
      <div
        ref={containerRef}
        style={{ position: "relative", width: "100%", height: "600px", border: "1px solid #ccc" }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        {nodes.map(node => (
          <NodeComponent
            key={node.id}
            node={node}
            onMouseDown={handleMouseDown}
            onClick={handleClick}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
          />
        ))}
      </div>
      {selectedNode && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h3>Edit Node Properties</h3>
          <div>
            <label>Type:</label>
            <select name="type" value={selectedNode.type} onChange={handleEditChange}>
              <option value="phase">phase</option>
              <option value="task">task</option>
              <option value="subFlow">subFlow</option>
              <option value="iterative">iterative</option>
              <option value="note">note</option>
              <option value="extra">extra</option>
            </select>
          </div>
          <div>
            <label>Label:</label>
            <input name="label" value={selectedNode.label} onChange={handleEditChange} />
          </div>
          <div>
            <label>Description:</label>
            <textarea name="description" value={selectedNode.description} onChange={handleEditChange} />
          </div>
          <div>
            <label>Link:</label>
            <input name="link" value={selectedNode.link} onChange={handleEditChange} />
          </div>
          <div>
            <label>Children (comma-separated IDs):</label>
            <input
              value={(selectedNode.children || []).join(",")}
              onChange={handleChildrenChange}
            />
          </div>
          <div>
            <label>Extras (comma-separated):</label>
            <input
              name="extras"
              value={(selectedNode.extras || []).join(",")}
              onChange={handleExtrasChange}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomFlowChartEditor;
