// src/pages/CustomFlowChartEditor.jsx
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

// ÁØÄÈªûÈ†êË®≠Â∞∫ÂØ∏
const DEFAULT_NODE_WIDTH = 140;
const DEFAULT_NODE_HEIGHT = 80;
const PHASE_DEFAULT_WIDTH = 300;
const PHASE_DEFAULT_HEIGHT = 200;
const RESIZE_HANDLE_SIZE = 10;
const PHASE_PADDING = 20;

const getNodeDimensions = (node) => {
  // Ëã•ÁØÄÈªûÂ∑≤Êúâ width/heightÔºåÂõûÂÇ≥ÔºõÂê¶Ââá‰æù type Áµ¶ÂÆöÈ†êË®≠ÂÄº
  if (node.width && node.height) return { width: node.width, height: node.height };
  if (node.type === 'phase') return { width: PHASE_DEFAULT_WIDTH, height: PHASE_DEFAULT_HEIGHT };
  return { width: DEFAULT_NODE_WIDTH, height: DEFAULT_NODE_HEIGHT };
};

// Ê†πÊìö type Áî¢ÁîüÁØÄÈªûÂÖßÂÆπËàáÊ®£Âºè
const renderNodeContent = (node) => {
  const commonStyle = { padding: "4px", textAlign: "center", fontSize: "12px", width: "100%", height: "100%" };
  switch (node.type) {
    case "phase":
      return (
        <div style={{
          ...commonStyle,
          border: "2px dashed blue",
          backgroundColor: "#e0f7ff",
          overflow: "hidden"
        }}>
          <div style={{ fontWeight: "bold", fontSize: "14px" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "task":
      return (
        <div style={{
          ...commonStyle,
          border: "2px solid black",
          backgroundColor: "#fff"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          {node.important && <div style={{ color: "red" }}>Important</div>}
          <div>{node.description}</div>
        </div>
      );
    case "subFlow":
      return (
        <div style={{
          ...commonStyle,
          border: "2px solid gray",
          backgroundColor: "#f0f0f0"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "iterative":
      return (
        <div style={{
          ...commonStyle,
          border: "2px dotted green",
          backgroundColor: "#e8ffe8"
        }}>
          <div style={{ fontWeight: "bold" }}>{node.label} üîÑ</div>
          <div>{node.description}</div>
        </div>
      );
    case "note":
      return (
        <div style={{
          ...commonStyle,
          border: "1px solid orange",
          backgroundColor: "#fff7d6"
        }}>
          <div style={{ fontStyle: "italic" }}>{node.text || node.label}</div>
          <div>{node.description}</div>
        </div>
      );
    case "extra":
      return (
        <div style={{ ...commonStyle, fontSize:"10px" }}>
          ‚Ä¢ {node.content}
        </div>
      );
    default:
      return (
        <div style={{ ...commonStyle, border: "1px solid black" }}>
          {node.label || "No Label"}
        </div>
      );
  }
};

// Ë®àÁÆó phase ÁØÄÈªûÈ°ØÁ§∫Â∞∫ÂØ∏ÔºöËá™ÂãïÂåÖ‰ΩèÂÖ∂Â≠êÁØÄÈªû
const computePhaseDisplayDimensions = (phase, allNodes) => {
  if (!phase.children || phase.children.length === 0) return getNodeDimensions(phase);
  // ÊâæÂá∫ÊâÄÊúâÂ±¨ÊñºË©≤ phase ÁöÑÂ≠êÁØÄÈªû
  const childrenNodes = allNodes.filter(n => phase.children.includes(n.id));
  if (childrenNodes.length === 0) return getNodeDimensions(phase);
  let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
  childrenNodes.forEach(child => {
    const dims = getNodeDimensions(child);
    minX = Math.min(minX, child.position.x);
    minY = Math.min(minY, child.position.y);
    maxX = Math.max(maxX, child.position.x + dims.width);
    maxY = Math.max(maxY, child.position.y + dims.height);
  });
  // ËÆì phase ÁöÑÂ∑¶‰∏äËßí‰øùÊåÅÂéü‰ΩçÁΩÆÔºåÊàñË™øÊï¥ÁÇ∫ËÉΩÂåÖ‰ΩèÂ≠êÁØÄÈªû
  const newWidth = Math.max(PHASE_DEFAULT_WIDTH, maxX - phase.position.x + PHASE_PADDING);
  const newHeight = Math.max(PHASE_DEFAULT_HEIGHT, maxY - phase.position.y + PHASE_PADDING);
  return { width: newWidth, height: newHeight };
};

const CustomFlowChartEditor = () => {
  const { id: projectId } = useParams();
  const token = localStorage.getItem('token');
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';
  const containerRef = useRef(null);

  // ÁØÄÈªûÁãÄÊÖãÔºåÊØèÂÄãÁØÄÈªûÂåÖÂê´ id, type, label, description, extras, link, children, position, width, height
  const [nodes, setNodes] = useState([]);
  // Ë¢´ÈÅ∏‰∏≠ÁØÄÈªû id
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  // ÊãñÊõ≥ÁãÄÊÖã
  const [draggingNodeId, setDraggingNodeId] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  // Ë™øÊï¥Â§ßÂ∞èÁãÄÊÖã
  const [resizingNodeId, setResizingNodeId] = useState(null);
  const [resizeStart, setResizeStart] = useState({ mouseX: 0, mouseY: 0, width: 0, height: 0 });

  // ËÆÄÂèñÂæåÁ´ØÊµÅÁ®ãÂúñË≥áÊñô
  useEffect(() => {
    const fetchFlowChart = async () => {
      try {
        const res = await axios.get(`${API_URL}/projects/${projectId}/flowchart`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setNodes(res.data || []);
      } catch (error) {
        console.error("Failed to load flowchart:", error);
      }
    };
    fetchFlowChart();
  }, [projectId, API_URL, token]);

  // ÊãñÊõ≥ÁØÄÈªûÔºöË®òÈåÑÊãñÊõ≥Ëµ∑ÂßãÈªûËàáÂÅèÁßªÈáè
  const handleMouseDown = (e, nodeId) => {
    e.stopPropagation();
    // Ëã•ÈªûÊìäÁöÑÊòØ resize handleÔºåÂâá‰∏çËôïÁêÜÊãñÊõ≥
    if (e.target.dataset.resize === "true") return;
    const nodeElem = e.currentTarget;
    const rect = nodeElem.getBoundingClientRect();
    setDraggingNodeId(nodeId);
    setDragOffset({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  // ÊãñÊõ≥ÈÅéÁ®ã‰∏≠Êõ¥Êñ∞ÁØÄÈªû‰ΩçÁΩÆ
  const handleMouseMove = (e) => {
    // Ë™øÊï¥Â§ßÂ∞èÂÑ™ÂÖàËôïÁêÜ
    if (resizingNodeId) {
      const deltaX = e.clientX - resizeStart.mouseX;
      const deltaY = e.clientY - resizeStart.mouseY;
      setNodes(prevNodes =>
        prevNodes.map(n =>
          n.id === resizingNodeId ? { 
            ...n, 
            width: Math.max(50, resizeStart.width + deltaX),
            height: Math.max(30, resizeStart.height + deltaY)
          } : n
        )
      );
      return;
    }
    if (!draggingNodeId) return;
    const containerRect = containerRef.current.getBoundingClientRect();
    const newX = e.clientX - containerRect.left - dragOffset.x;
    const newY = e.clientY - containerRect.top - dragOffset.y;
    setNodes(prevNodes =>
      prevNodes.map(n => {
        if (n.id === draggingNodeId) {
          // Â¶ÇÊûúÊãñÊõ≥ÁöÑÊòØ phaseÔºåÁßªÂãï phaseÂêåÊôÇÁßªÂãïÂÖ∂Â≠êÁØÄÈªû
          const deltaX = newX - n.position.x;
          const deltaY = newY - n.position.y;
          if (n.type === 'phase' && n.children && n.children.length > 0) {
            return { ...n, position: { x: newX, y: newY } };
          }
          return { ...n, position: { x: newX, y: newY } };
        }
        // Ëã•Ê≠§ÁØÄÈªûÊòØË¢´ÊüêÂÄã phaseÊãñÂãïÊôÇÁöÑÂ≠êÁØÄÈªûÔºà‰∏îÈùûÁç®Á´ãÊãñÊõ≥ÔºâÔºåÊõ¥Êñ∞ÂÖ∂‰ΩçÁΩÆ
        // ÂèØÈÄ≤‰∏ÄÊ≠•ÂØ¶‰ΩúÔºöËã•ÊãñÊõ≥ phaseÊôÇËá™ÂãïÊõ¥Êñ∞ÊâÄÊúâÂ±¨ÊñºË©≤ phaseÁöÑÂ≠êÁØÄÈªû
        return n;
      })
    );
  };

  const handleMouseUp = () => {
    setDraggingNodeId(null);
    setResizingNodeId(null);
  };

  // ÈªûÈÅ∏ÁØÄÈªû‰ª•‰æõÁ∑®ËºØ
  const handleSelectNode = (e, nodeId) => {
    e.stopPropagation();
    setSelectedNodeId(nodeId);
  };

  // Âà™Èô§ÁØÄÈªûÔºåÂêåÊôÇÂæûÊâÄÊúâÁà∂ÁØÄÈªûÁöÑ children Âà™Èô§
  const handleDeleteNode = (nodeId) => {
    setNodes(prevNodes =>
      prevNodes
        .filter(n => n.id !== nodeId)
        .map(n => n.children ? { ...n, children: n.children.filter(childId => childId !== nodeId) } : n)
    );
    if (selectedNodeId === nodeId) setSelectedNodeId(null);
  };

  // Ë™øÊï¥Â§ßÂ∞èÔºöÈñãÂßãÊãñÂãï resize handle
  const handleResizeMouseDown = (e, nodeId) => {
    e.stopPropagation();
    setResizingNodeId(nodeId);
    setResizeStart({
      mouseX: e.clientX,
      mouseY: e.clientY,
      width: getNodeDimensions(nodes.find(n => n.id === nodeId)).width,
      height: getNodeDimensions(nodes.find(n => n.id === nodeId)).height
    });
  };

  // ÂÅ¥ÈÇäÁ∑®ËºØË°®ÂñÆÊõ¥Êñ∞ÁØÄÈªûÂ±¨ÊÄßÔºàÁõ¥Êé•Â±¨ÊÄßÔºâ
  const handleEditChange = (e) => {
    if (!selectedNodeId) return;
    const { name, value } = e.target;
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, [name]: value } : n
      )
    );
  };

  // ÁâπÂà•ËôïÁêÜ children Â±¨ÊÄßÔºà‰ª•ÈÄóËôüÂàÜÈöîËΩâÁÇ∫Èô£ÂàóÔºâ
  const handleChildrenChange = (e) => {
    if (!selectedNodeId) return;
    const children = e.target.value.split(",").map(s => s.trim()).filter(s => s);
    setNodes(prevNodes =>
      prevNodes.map(n =>
        n.id === selectedNodeId ? { ...n, children } : n
      )
    );
  };

  // Êñ∞Â¢ûÁØÄÈªû
  const handleAddNode = () => {
    const newNode = {
      id: `${Date.now()}`,
      type: "task",  // È†êË®≠ type ÁÇ∫ task
      label: "New Node",
      description: "",
      extras: [],
      link: "",
      children: [],
      position: { x: 50, y: 50 },
      width: DEFAULT_NODE_WIDTH,
      height: DEFAULT_NODE_HEIGHT
    };
    setNodes(prev => [...prev, newNode]);
  };

  // Ë®àÁÆó phase ÁØÄÈªûËá™ÂãïÂåÖ‰ΩèÂ≠êÁØÄÈªûÁöÑÈ°ØÁ§∫Â∞∫ÂØ∏
  const getPhaseDisplayDimensions = (phase) => {
    const base = getNodeDimensions(phase);
    if (!phase.children || phase.children.length === 0) return base;
    const childrenNodes = nodes.filter(n => phase.children.includes(n.id));
    if (childrenNodes.length === 0) return base;
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
    childrenNodes.forEach(child => {
      const dims = getNodeDimensions(child);
      minX = Math.min(minX, child.position.x);
      minY = Math.min(minY, child.position.y);
      maxX = Math.max(maxX, child.position.x + dims.width);
      maxY = Math.max(maxY, child.position.y + dims.height);
    });
    // phase Â∑¶‰∏äËßí‰∏çËÆäÔºåÂØ¨È´òË™øÊï¥ÁÇ∫ËÉΩÂåÖ‰ΩèÊâÄÊúâÂ≠êÁØÄÈªûÔºåÂä†‰∏ä‰∫õË®± padding
    return { width: Math.max(base.width, maxX - phase.position.x + PHASE_PADDING),
             height: Math.max(base.height, maxY - phase.position.y + PHASE_PADDING) };
  };

  // ÂÑ≤Â≠òÊµÅÁ®ãÂúñÂà∞ÂæåÁ´Ø
  const handleSave = async () => {
    try {
      await axios.put(`${API_URL}/projects/${projectId}/flowchart`,
        { flowChart: nodes },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flowchart saved successfully!");
    } catch (error) {
      console.error("Failed to save flowchart:", error);
      alert("Save failed.");
    }
  };

  // Ê∏≤ÊüìÈÄ£Á∑öÔºöÊ†πÊìöÊØèÂÄãÁØÄÈªûÁöÑ childrenÔºåÂæûÁà∂ÁØÄÈªû‰∏≠ÂøÉÈÄ£Á∑öÂà∞Â≠êÁØÄÈªû‰∏≠ÂøÉ
  const renderConnections = () => {
    const lines = [];
    nodes.forEach(parent => {
      if (parent.children && Array.isArray(parent.children)) {
        parent.children.forEach(childId => {
          const child = nodes.find(n => n.id === childId);
          if (child) {
            const parentDims = parent.type === 'phase' ? getPhaseDisplayDimensions(parent) : getNodeDimensions(parent);
            const childDims = getNodeDimensions(child);
            const startX = parent.position.x + parentDims.width / 2;
            const startY = parent.position.y + parentDims.height / 2;
            const endX = child.position.x + childDims.width / 2;
            const endY = child.position.y + childDims.height / 2;
            lines.push(
              <line
                key={`line-${parent.id}-${child.id}`}
                x1={startX} y1={startY} x2={endX} y2={endY}
                stroke="black" strokeWidth="2"
              />
            );
          }
        });
      }
    });
    return lines;
  };

  const selectedNode = nodes.find(n => n.id === selectedNodeId);

  return (
    <div>
      <h2>Custom Flow Chart Editor</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={handleAddNode}>Add Node</button>
        <button onClick={handleSave} style={{ marginLeft: "10px" }}>Save Flow Chart</button>
      </div>
      <div
        ref={containerRef}
        style={{ position: "relative", width: "100%", height: "600px", border: "1px solid #ccc" }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        <svg style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%" }}>
          {renderConnections()}
        </svg>
        {nodes.map(node => {
          // Â¶ÇÊûúÊòØ phaseÔºåÂãïÊÖãË®àÁÆóÈ°ØÁ§∫Â∞∫ÂØ∏
          const dims = node.type === 'phase' ? getPhaseDisplayDimensions(node) : getNodeDimensions(node);
          return (
            <div
              key={node.id}
              style={{
                position: "absolute",
                left: node.position.x,
                top: node.position.y,
                width: dims.width,
                height: dims.height,
                boxSizing: "border-box",
                border: selectedNodeId === node.id ? "2px solid blue" : "none",
                backgroundColor: "transparent",
                cursor: "move"
              }}
              onMouseDown={(e) => handleMouseDown(e, node.id)}
              onClick={(e) => handleSelectNode(e, node.id)}
            >
              {renderNodeContent(node)}
              {/* Âà™Èô§ÊåâÈàï */}
              <button
                style={{
                  position: "absolute",
                  top: 0,
                  right: 0,
                  backgroundColor: "red",
                  color: "#fff",
                  border: "none",
                  cursor: "pointer",
                  fontSize: "10px",
                  padding: "2px"
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteNode(node.id);
                }}
              >
                X
              </button>
              {/* Ëã•Ê≠§ÁØÄÈªûÁÇ∫Ë¢´ÈÅ∏‰∏≠ÔºåÂä†ÂÖ• resize handle */}
              {selectedNodeId === node.id && (
                <div
                  data-resize="true"
                  style={{
                    position: "absolute",
                    right: 0,
                    bottom: 0,
                    width: RESIZE_HANDLE_SIZE,
                    height: RESIZE_HANDLE_SIZE,
                    backgroundColor: "gray",
                    cursor: "nwse-resize"
                  }}
                  onMouseDown={(e) => handleResizeMouseDown(e, node.id)}
                />
              )}
            </div>
          );
        })}
      </div>
      {/* ÂÅ¥ÈÇäÁ∑®ËºØË°®ÂñÆ */}
      {selectedNode && (
        <div style={{ marginTop: "20px", border: "1px solid #ccc", padding: "10px" }}>
          <h3>Edit Node Properties</h3>
          <div>
            <label>Type:</label>
            <select name="type" value={selectedNode.type} onChange={handleEditChange}>
              <option value="phase">phase</option>
              <option value="task">task</option>
              <option value="subFlow">subFlow</option>
              <option value="iterative">iterative</option>
              <option value="note">note</option>
              <option value="extra">extra</option>
            </select>
          </div>
          <div>
            <label>Label:</label>
            <input name="label" value={selectedNode.label} onChange={handleEditChange} />
          </div>
          <div>
            <label>Description:</label>
            <textarea name="description" value={selectedNode.description} onChange={handleEditChange} />
          </div>
          <div>
            <label>Link:</label>
            <input name="link" value={selectedNode.link} onChange={handleEditChange} />
          </div>
          <div>
            <label>Children (comma-separated IDs):</label>
            <input
              value={(selectedNode.children || []).join(",")}
              onChange={handleChildrenChange}
            />
          </div>
          <div>
            <label>Extras (comma-separated):</label>
            <input
              name="extras"
              value={(selectedNode.extras || []).join(",")}
              onChange={(e) => {
                const arr = e.target.value.split(",").map(s => s.trim()).filter(s => s);
                handleEditChange({ target: { name: "extras", value: arr } });
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomFlowChartEditor;
