// pages/HomePage.jsx
import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div style={{ display: 'flex' }}>
      {/* 左側主選單 */}
      <div style={{ width: '200px', background: '#eee', height: '100vh' }}>
        <ul>
          <li><Link to="/projects">Mail</Link></li>
          <li><Link to="/mail">Mail</Link></li>
          <li><Link to="/internal-files">內部檔案</Link></li>
          <li><button onClick={handleLogout}>登出</button></li>
        </ul>
      </div>

      {/* 右側可視化管理區 */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h1>專案管理（便利貼式介面）</h1>
        <p>在這裡可放置便條紙或看板 (Kanban) 風格的 UI</p>
      </div>
    </div>
  );
}

export default HomePage;
