// client/src/pages/HomePage.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchAllProjects } from '../api/project';

function HomePage() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  // componentDidMount
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      // 如果沒有 token，代表尚未登入，導回登入頁面
      window.location.href = '/login';
      return;
    }
    // 從後端取得專案清單
    fetchProjects(token);
  }, []);

  const fetchProjects = async (token) => {
    try {
      const data = await fetchAllProjects(token);
      setProjects(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setLoading(false);
      alert('無法取得專案清單，請確認後端是否已啟動');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  if (loading) {
    return <div>載入中...</div>;
  }

  return (
    <div style={{ display: 'flex' }}>
      {/* 左側主選單 */}
      <div style={{ width: '200px', background: '#f2f2f2', minHeight: '100vh', padding: '10px' }}>
        <h3>主選單</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li>
            <Link to="/mail">Mail</Link>
          </li>
          <li>
            <Link to="/internal-files">內部檔案</Link>
          </li>
          <li>
            <button onClick={handleLogout}>登出</button>
          </li>
        </ul>
      </div>

      {/* 右側內容區：顯示專案清單 */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h1>專案清單</h1>
        {projects.length === 0 ? (
          <p>目前沒有任何專案</p>
        ) : (
          <ul>
            {projects.map((project) => (
              <li key={project._id}>
                {/* 假設你有設定 /projects/:id 的路由 */}
                <Link to={`/projects/${project._id}`}>
                  {project.clientName} - {project.projectName}
                </Link>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default HomePage;
